import { useState, useEffect } from "react";

const Time = (props) => {
    const [meridiem, setMeridiem] = useState(''); //AM or PM

    //Assumption is that props.number is a string in the form 'HH:MM' where HH is the hour and MM is the minute
    const number = parseInt(props.number.slice(0,2)); 

    //Offset is the offset number (+ or -) that converts this time from the UTC timezone to the local time
    const offset = props.offset;
    
    //convert from military time
    if(number > 12) setMeridiem('PM');
    else setMeridiem ('AM');
    const regularTime = number > 12 ?  number - 12 + ' PM': number === 0 ? 12 + ' AM' : number + ' AM';


    
    return regularTime;
}

export default Time
