import Select from 'react-select'
import { useGetCitiesQuery } from '../Features/locations/locationApiSlice'

import './../Styles/addLocation.css'


const SelectCity = (props) => {

    const { currentData, isSuccess } = useGetCitiesQuery({ "country": props.country, "state": props.state });
    const cities = isSuccess ? currentData.data : [];
    const optionsCities = [];
    cities.forEach(item => {
        optionsCities.push({
            value: item.city,
            label: item.city
        });
    });
/*     optionsCities.push({
        value: null,
        label: 'N/A'
    }) */
    return (
        <div>
            <span>City</span>
            <br />
            <Select
                options={optionsCities}
                onChange={(newValue) => props.setCity(newValue.value)}
            />
            <span className='note'>Note: If no options for cities, then there are no active stations reporting weather data in that state.</span>
        </div>
    )
}

export default SelectCity
