import '../../Styles/addLocation.css'
import { useState } from 'react'

import AddLocationManual from './AddLocationManual';
import AddLocationCoordinates from './AddLocationCoordinates';
import AddLocationCurrentLocation from './AddLocationCurrentLocation';


const AddLocation = (props) => {

  const [addLocationManualMenu, setAddLocationManualMenu] = useState(false);
  const [addLocationCoordinatesMenu, setAddLocationCoordinatesMenu] = useState(false);
  const [addLocationCurrentLocationMenu, setAddLocationCurrentLocationMenu] = useState(false);

  const manualMenu = addLocationManualMenu ? <AddLocationManual/> : <></>;
  const coordinatesMenu = addLocationCoordinatesMenu ? <AddLocationCoordinates/> : <></>;
  const currentLocationMenu = addLocationCurrentLocationMenu ? <AddLocationCurrentLocation/> : <></>;


  return (
    <div className="addLocationMoodle">
      <div id="addLocationOptionsButtons">
        <button className='addLocationButton'>Use my current location</button>
        <br />
        <button className='addLocationButton'>Add by country/state/city</button>
        <br />
        <button className='addLocationButton'>Add by coordinates</button>
        <br />
        <button className='addLocationBackButton' onClick={() => props.onClose()}>Close</button>
      </div>

    </div>
  )
}

export default AddLocation
