import { useState, useEffect } from "react";
import processed_locations from '../../data/processed_locations.json'

import '../../Styles/searchBar.css';

const SearchLocation = () => {

    const [isFocused, setIsFocused] = useState(false);
    const [searchValue, setSearchValue] = useState(null);
    const initialQuery = []; 
    const [queryResult, setQueryResult] = useState(initialQuery);

    const handleChange = (newValue) => {
        setSearchValue(newValue);
    }

    useEffect(() => {
        queryLocations(searchValue);

    }, [searchValue])

    const queryLocations = (query) => {

        if (query === '' || query === null || query === undefined) return;

        const lowercaseQuery = query.toLowerCase();
        const regexQuery = new RegExp(lowercaseQuery);

        const countriesQueryResults = []
        processed_locations.forEach((country) => {
            if (countriesQueryResults.length === 10) return; //only return first 10 results
            const lowercaseCountry = country.name.toLowerCase();
            if (regexQuery.test(lowercaseCountry)) {
                countriesQueryResults.push(country.name);
                console.log(country.name)
            }
        })
        const newQueryResult = queryResult;
        newQueryResult.countries = countriesQueryResults;
        setQueryResult(newQueryResult);
    }


    const countriesQueryResultsElements = queryResult.length === 0 ? <></> : queryResult.map((item, index) =>
        <div className="queryResult" key={index} >{item}</div>
    );

    const dropDown = isFocused ? <div className="dropdownBorder">
        <label className="dropdownLabel" htmlFor="dropdownResultsCountry" ><b>Choose a city:</b></label>
        <div className="dropdownResults" id="dropdownResultsCountry">{countriesQueryResultsElements}</div>
    </div> : <></>;

    return (
        <div id="location-search-container">
            <input id="location-search"
                type="text"
                name="cities"
                placeholder={"Search for a city..."}
                onChange={(e) => handleChange(document.getElementById("location-search").value)}
                onFocus={() => setIsFocused(true)}
                onBlur={() => setIsFocused(false)}
            />
            {dropDown}
        </div>

    )
}

export default SearchLocation
