import { useRef, useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { selectCurrentUser, setCredentials } from '../../Features/auth/authSlice';
import { useLoginMutation, useGetUserLocationsQuery } from '../../Features/auth/authApiSlice';
import { setTheme, setTempUnit } from '../../Features/user_preferences/preferenceSlice';

import '../../Styles/login.css';
import '../../Styles/app.css';


const Login = () => {
    const userRef = useRef();
    const errRef = useRef();
    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const navigate = useNavigate();

    //login is the trigger function for the mutation hook
    //for more info on the return of useLoginMutation(): 
    //https://redux-toolkit.js.org/rtk-query/usage/mutations#frequently-used-mutation-hook-return-values
    //(remember useLoginMutation is auto generated by RTK Query from 'login' endpoint)
    const [login, { isLoading }] = useLoginMutation();
    const dispatch = useDispatch();

    const loggedInUser = useSelector(selectCurrentUser);
    const skipUserLocations = loggedInUser ? false : true;
    const { data, isLoading: isLoadingUserLocations, isSuccess } = useGetUserLocationsQuery({ 'username': loggedInUser }, { skip: skipUserLocations });

    useEffect(() => {
        if (isSuccess) {
            const locationsFormattedCorrectly = [];
            data?.locations.forEach(location => {
                locationsFormattedCorrectly.push({
                    "city":{
                        "name": location.city
                    },
                    "province":{
                        "name": location.province,
                        "state_code": location.state_code
                    },
                    "country":{
                        "name": location.country,
                        "iso2": location.iso2
                    }
                })
            });

            //once locations are set, navigate to new page
            //navigate('/locations')
        }
    }, [isSuccess])

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const setUserLocations = (user) => {

    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            //send request to backend to see if user has correct credentials
            const userData = await login({ 'username': user, 'password': pwd }).unwrap();
            const accessToken = userData.access_token;
            //set application logic
            dispatch(setCredentials({ user, accessToken }));

            //fetch user prefs if they exist
            const tempUnit = localStorage.getItem("temperatureUnit");
            const theme = localStorage.getItem("theme");
            if (tempUnit === 'Celcius' || tempUnit === 'Fahrenheit') {
                dispatch(setTempUnit({ "unit": tempUnit }));
            }
            if (theme === "Light" || theme === "Dark") {
                dispatch(setTheme({ "theme": theme }));
            }

            //reset page logic (not sure if neccesary?)
            setUser('');
            setPwd('');

        }
        catch (err) {
            if (err?.data?.message) {
                setErrMsg(err.data.message);
            } else {
                setErrMsg('No server response.');
            }
            errRef.current.focus();
        }
    }

    const handleUserInput = (e) => setUser(e.target.value);
    const handlePwdInput = (e) => setPwd(e.target.value);

    const content = isLoading ? <h1 className='login'>loading...</h1> : (
        <section className='login'>
            <p ref={errRef} className={errMsg !== '' ? "errmsg" : "offscreen"}> {errMsg}</p>

            <h1>Login</h1>

            <form className='loginForm' onSubmit={handleSubmit}>
                <label htmlFor='username'>Username:</label>
                <input
                    type="text"
                    id="username"
                    ref={userRef}
                    value={user}
                    onChange={handleUserInput}
                    autoComplete="off"
                    required
                />
                <label htmlFor='password'>Password:</label>
                <input
                    type="password"
                    id="password"
                    onChange={handlePwdInput}
                    value={pwd}
                    required
                />
                <button>Sign In</button>
            </form>
            <span>Dont have an account? <Link to='/register'>Register now.</Link></span>
        </section>
    )

    return content;
}

export default Login;
