import React, { useState } from 'react'
import Select from 'react-select'
import { useGetCountriesQuery } from '../Features/locations/locationApiSlice'

const AddLocation = () => {

  const { data, isLoading } = useGetCountriesQuery()
  const countries = isLoading ? [] : data.data; //data is the response from the backend, data.data is the list of countries
  const options = []
  countries.forEach(item => {
    options.push({
      value: item.country,
      label: item.country
    })
  })

  const [country, setCountry] = useState();

  const onInputChange = ( inputValue,{ action, prevInputValue }) => {
    if (action === 'input-change'){
      setCountry(inputValue)
      console.log(country);
      return inputValue;
    }
    return prevInputValue;
  };

  return (
    <div>
      <button>Add my location</button>
      <br />
      <span>- OR -</span>
      <br />
      <span>Add a location manually</span>
      <br />
      <span> Country </span>
      <Select 
      options={options}
      getOptionValue= {option => {setCountry(option)}}
      />
    </div>
  )
}

export default AddLocation
