import { useState, useEffect } from "react";

const Time = (props) => {
    
    const [meridiem, setMeridiem] = useState(''); //AM or PM

    //Assumption is that props.number is a string in the form 'HH:MM' where HH is the hour and MM is the minute
    const number = parseInt(props.number.slice(0, 2));

    console.log(number);

    //Offset is the offset number (+ or -) that converts this time from the UTC timezone to the local time
    const offset = props.offset;

    useEffect(() => {
        //convert from military time on initial render
        if (number + offset > 12) setMeridiem('PM');
        else setMeridiem('AM');
    }, [])

    const offsetTime = Math.abs(number + offset) % 12;
    console.log('after mod: ' + offsetTime );

    const convertedTimeWithMeridiem = offsetTime === 0 ? 12 + ' ' + meridiem : offsetTime + ' ' + meridiem;

    console.log(convertedTimeWithMeridiem);


    return convertedTimeWithMeridiem;
}

export default Time
