import { useState, useEffect } from "react";
import processed_locations from '../../data/processed_locations.json'

const SearchLocation = () => {

    const [searchValue, setSearchValue] = useState(null);
    const [queryResult, setQueryResult] = useState([]);

    const QUERY_TYPE = {
        COUNTRY: "COUNTRY",
        STATE: "STATE",
        CITY: "CITY"
    }

    const handleChange = (newValue) => {
        setSearchValue(newValue);
    }

    useEffect(() => {
        queryLocations(searchValue, QUERY_TYPE.COUNTRY);
        queryLocations(searchValue, QUERY_TYPE.STATE);
        queryLocations(searchValue, QUERY_TYPE.CITY);
    }, [searchValue])

    const queryLocations = (query, type) => {
        switch (type) {
            case QUERY_TYPE.COUNTRY:
                // code block
                console.log("switch country");
                break;
            case QUERY_TYPE.STATE:
                // code block
                console.log("switch state");
                break;
            case QUERY_TYPE.CITY:
                // code block
                console.log("switch city");
                break;
        }
    }

    const dropDown = <div className="dropdownBorder">
        <label className="dropdownLabel" >Country</label>
        <div className="dropdownResults"></div>

        <label className="dropdownLabel" >State</label>
        <div className="dropdownResults"></div>

        <label className="dropdownLabel" >City</label>
        <div className="dropdownResults"></div>
    </div>

    return (
        <div id="location-search-container">
            <input id="location-search"
                type="text"
                name="cities"
                placeholder={"Search country, state, city... "}
                onChange={(e) => handleChange(document.getElementById("location-search").value)}
            />
        </div>

    )
}

export default SearchLocation
