import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    locations: [],
    currentLocation: null
};

const locationsSlice = createSlice({
    name: 'locations',
    initialState,
    reducers: {
        setUserLocations: (state, action) => {
            const { locations } = action.payload;
            state.locations = locations;

        },
        addLocation: (state, action) => {
            const { city, province, country } = action.payload;
            state.locations = [...state.locations, {
                "city": city,
                "province": province,
                "country": country
            }]
        },
        setCurrentLocation: (state, action) => {
            const { city, province, country } = action.payload;
            state.currentLocation = {
                "city":{
                    "name": city.name
                },
                "province":{
                    "name": province.name,
                    "state_code": province.state_code
                },
                "country":{
                    "name": country.name,
                    "iso2": country.iso2 //country 2 letter code
                }
            }
        }
    }
});

export const { addLocation, setUserLocations, setCurrentLocation } = locationsSlice.actions;

export default locationsSlice.reducer;

export const selectAllLocations = (state) => state.locations.locations;

export const selectCurrentLocation = (state) => {
    return {
        "city": state.currentLocation?.city,
        "province": state.currentLocation?.province,
        "country": state.currentLocation?.country
    }
}
