import { useState, useEffect } from "react";
import processed_locations from '../../data/processed_locations.json'

import '../../Styles/searchBar.css';

const SearchLocation = () => {

    const [searchValue, setSearchValue] = useState(null);
    const initialQuery = {
        'countries': [],
        'states': [],
        'cities': []
    }
    const [queryResult, setQueryResult] = useState(initialQuery);

    const QUERY_TYPE = {
        COUNTRY: "COUNTRY",
        STATE: "STATE",
        CITY: "CITY"
    }

    const handleChange = (newValue) => {
        setSearchValue(newValue);
    }

    useEffect(() => {
        queryLocations(searchValue, QUERY_TYPE.COUNTRY);
        queryLocations(searchValue, QUERY_TYPE.STATE);
        queryLocations(searchValue, QUERY_TYPE.CITY);
    }, [searchValue])

    const queryLocations = (query, type) => {

        if(query === '' || query === null || query === undefined) return; 

        const lowercaseQuery = query.toLowerCase();
        const regexQuery = new RegExp(lowercaseQuery);

        switch (type) {
            case QUERY_TYPE.COUNTRY:

                const countriesQueryResults = []
                processed_locations.forEach((country)=>{
                    if(countriesQueryResults.length === 10) return; //only return first 10 results
                    const lowercaseCountry = country.name.toLowerCase();
                    if(regexQuery.test(lowercaseCountry)){
                        countriesQueryResults.push(country.name);
                    }
                })
                
                break;

            case QUERY_TYPE.STATE:
                //console.log("switch state");
                break;

            case QUERY_TYPE.CITY:
                //console.log("switch city");

                break;
        }
    }

    const countriesQueryResultsElements = queryResult.countries.length === 0 ? <></> : queryResult.countries.map((item, index)=>{
        <div>item</div>
    });
    const statesQueryResultsElements = <></>;
    const citiesQueryResultsElements = <></>;

    const dropDown = <div className="dropdownBorder">
        <label className="dropdownLabel" htmlFor="dropdownResultsCountry" >Country</label>
        <div className="dropdownResults" id="dropdownResultsCountry">{countriesQueryResultsElements}</div>

        <label className="dropdownLabel" >State</label>
        <div className="dropdownResults" id="dropdownResultsState"></div>

        <label className="dropdownLabel" >City</label>
        <div className="dropdownResults" id="dropdownResultsCity"></div>
    </div>

    return (
        <div id="location-search-container">
            <input id="location-search"
                type="text"
                name="cities"
                placeholder={"Search country, state, city... "}
                onChange={(e) => handleChange(document.getElementById("location-search").value)}
            />
        </div>

    )
}

export default SearchLocation
