import { useState, useEffect } from "react";
import processed_locations from '../../data/processed_locations.json'

import '../../Styles/searchBar.css';

const SearchLocation = () => {

    const [isFocused, setIsFocused] = useState(false);
    const [searchValue, setSearchValue] = useState(null);
    const initialQuery = [];
    const [queryResult, setQueryResult] = useState(initialQuery);

    const AMOUNT_OF_RESULTS_TO_DISPLAY = 10;

    const handleChange = (newValue) => {
        setSearchValue(newValue);
    }

    useEffect(() => {
        queryLocations(searchValue);

    }, [searchValue])

    const doWordArraysMatch = (userQueryWordsArray, locationWordsArray, startIndex = 0, endIndex = userQueryWordsArray.length) => {
        for (let i = startIndex; i < endIndex; i++) {
            const regexExpr = '^' + userQueryWordsArray[i];
            const regexQuery = new RegExp(regexExpr);
            if (!regexQuery.test(locationWordsArray[i])) {
                return false;
            }
        }
        return true;
    }

    const queryLocations = (query) => {

        if (query === '' || query === null || query === undefined) return;

        const queryWithoutPunc = query.replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g, "");
        const queryLowercase = queryWithoutPunc.toLowerCase();
        const queryArray = queryLowercase.split(/\s+/);


        const citiesQueryResults = []
        processed_locations.forEach(country => {
            country.states.forEach(state => {
                state.cities.forEach(city => {

                    const lowercaseCity = city.toLowerCase();
                    const cityArray = lowercaseCity.split(/\s+/);
                    const numCityWords = cityArray.length;

                    const lowercaseState = state.name.toLowerCase();
                    const stateArray = lowercaseState.split(/\s+/);
                    const numStateWords = stateArray.length;

                    const lowercaseCountry = country.name.toLowerCase();
                    const countryArray = lowercaseCountry.split(/\s+/);
                    const numCountryWords = countryArray.length;


                    const regexExpr = '^' + queryArray[0];
                    const regexQuery = new RegExp(regexExpr);
                    let doesCityMatch = false;

                    //this search assumes the user types in the city name first

                    //initial filter to lower matching results
                    if (regexQuery.test(lowercaseCity)) {
                        doesCityMatch = true;

                        //2 cases if there is a match:
                        //user query has less words than city name
                        //or user query has more words than city name
                        //in either case check if it matches the city first
                        if (queryArray.length < numCityWords) {

                            //check if each word in the city matches each word in the query
                            doesCityMatch = doWordArraysMatch(queryArray, cityArray)
                        }
                        else if (queryArray.length == numCityWords && queryLowercase === lowercaseCity);//returns true
                        else {
                            doesCityMatch = doWordArraysMatch(queryArray, cityArray, endIndex=numCityWords)
                            //check if each word in the city matches each word in the query
                            for (let i = 0; i < numCityWords; i++) {
                                const regexExpr = '^' + queryArray[i]; 
                                const regexQuery = new RegExp(regexExpr);
                                if (!regexQuery.test(cityArray[i])) {
                                    doesCityMatch = false;
                                    break;
                                }
                            }

                            //queryArray.length is guarenteed to be greater than numCityWords at this point
                            const numWordsOverCityWordLength = queryArray.length - numCityWords;

                            //now we must consider matching the remaining words in the user query 
                            //with the state and country of the city

                            if (numStateWords > 1) {
                                for (let i = numCityWords; i < queryArray.length; i++) {
                                    const regexExpr = '^' + queryArray[i];
                                    const regexQuery = new RegExp(regexExpr);

                                    const stateIndex = i - numCityWords;
                                    if (!regexQuery.test(stateArray[stateIndex])) {
                                        doesCityMatch = false;
                                        break;
                                    }
                                }
                            }
                            else {

                            }

                            if (numCountryWords > 1) {

                            }
                            else {

                            }
                            for (let i = 1; i < queryArray.length; i++) {
                                const regexExpr = '^' + queryArray[i];
                                const regexQuery = new RegExp(regexExpr);
                                if (!regexQuery.test(lowercaseState) || !regexQuery.test(lowercaseCountry)) {
                                    doesCityMatch = false;
                                    break;
                                }
                            }
                        }
                    }
                    if (doesCityMatch) {
                        citiesQueryResults.push({
                            'location': {
                                'city': city,
                                'state': state.name,
                                'country': country.name
                            }
                        });
                    }
                })
            })

        })
        const topResults = [];
        citiesQueryResults.slice(0, AMOUNT_OF_RESULTS_TO_DISPLAY - 1).map(item => topResults.push(item))
        setQueryResult(topResults);
    }

    const handleDropDownSelect = (city, state, country) => {

    }

    const countriesQueryResultsElements = queryResult.length === 0 ? <></> : queryResult.map((item, index) =>
        <div
            className="queryResult"
            key={index}
            onClick={() => handleDropDownSelect(item.location.city, item.location.state, item.location.country)}
        >
            {'' + item.location.city + ', ' + item.location.state + ', ' + item.location.country}
        </div>
    );

    const dropDown = isFocused ? <div className="dropdownBorder border">
        <label className="dropdownLabel" htmlFor="dropdownResultsCountry" ><b>Choose a city:</b></label>
        <div className="dropdownResults ">{countriesQueryResultsElements}</div>
    </div> : <></>;

    return (
        <div id="location-search-container">
            <input id="location-search"
                type="text"
                name="cities"
                placeholder={"Search for a city..."}
                onChange={(e) => handleChange(document.getElementById("location-search").value)}
                onFocus={() => setIsFocused(true)}
                onBlur={() => setIsFocused(false)}
                maxLength={100}
            />
            {dropDown}
        </div>

    )
}

export default SearchLocation
