import { Settings, ToggleOff, ToggleOn } from "../../Icons/svgImages/index";
import { logOut,selectCurrentToken } from "../../Features/auth/authSlice";
import { selectTempUnit, selectTheme } from "../../Features/user_preferences/preferenceSlice";
import { useLogoutMutation } from "../../Features/auth/authApiSlice";

import { useSelector, useDispatch } from "react-redux";

import { useEffect, useState } from "react";

import '../../Styles/settings.css';

const SettingsButton = () => {

    const accessToken = useSelector(selectCurrentToken);
    const dispatch = useDispatch();
    const [logout] = useLogoutMutation();

    const [isOpen, setIsOpen] = useState(false);
    const [tempUnit, setTempUnit] = useState();
    const [themeSetting, setThemeSetting]= useState();

    const temp = useSelector(selectTempUnit);
    const theme = useSelector(selectTheme);

    useEffect(()=>{
        setTempUnit(temp);
    },[temp])

    useEffect(()=>{
        setThemeSetting(theme)
    },[theme])

    const handleLogout = async () => {
        try {
          const message = await logout({ accessToken }).unwrap();
          dispatch(logOut());
          console.log(message);
        }
        catch (err) {
          console.log(err);
          console.log("something went wrong");
        }
    }
    const toggleOpen = () =>{
        if(isOpen) setIsOpen(false);
        else setIsOpen(true);
    }
    const tempToggleIcon = tempUnit === "Celcius" ? <ToggleOn/> : <ToggleOff/>;
    const themeToggleIcon = themeSetting === "Light" ? <ToggleOn/> : <ToggleOff/>;

    const handleClickTemp = () =>{
        if(tempUnit === "Celcius"){
            dispatch(setTempUnit("Fahrenheit"))
        }
    }
    const handleClickTheme = () =>{

    }
    
    const dropDown = isOpen ? <div className="settings-drop-down border">
        <div className="settings-drop-down-option" >Â° Unit: {temp} {tempToggleIcon}</div>
        <div className="settings-drop-down-option">Theme: {theme} {themeToggleIcon}</div>
        <div className="settings-drop-down-option" onClick={() => handleLogout()} >Logout</div>
    </div> : <></>


    return <div>
        <Settings onClick={toggleOpen} className="settings-icon" />
        {dropDown}
    </div>
}
export default SettingsButton
