import { Settings, ToggleOff, ToggleOn } from "../../Icons/svgImages/index";
import { logOut,selectCurrentToken } from "../../Features/auth/authSlice";
import { selectTempUnit, selectTheme } from "../../Features/user_preferences/preferenceSlice";
import { useLogoutMutation } from "../../Features/auth/authApiSlice";

import { useSelector, useDispatch } from "react-redux";

import { useState } from "react";

import '../../Styles/settings.css';

const SettingsButton = () => {

    const accessToken = useSelector(selectCurrentToken);
    const dispatch = useDispatch();
    const [logout] = useLogoutMutation();

    const [isOpen, setIsOpen] = useState(false);
    const [tempUnit, setTempUnit] = useState();

    const temp = useSelector(selectTempUnit);
    const theme = useSelector(selectTheme);

    const handleLogout = async () => {
        try {
          const message = await logout({ accessToken }).unwrap();
          dispatch(logOut());
          console.log(message);
        }
        catch (err) {
          console.log(err);
          console.log("something went wrong");
        }
    }
    const toggleOpen = () =>{
        if(isOpen) setIsOpen(false);
        else setIsOpen(true);
    }
    const tempToggleIcon = <></>
    
    const dropDown = isOpen ? <div className="settings-drop-down border">
        <div className="settings-drop-down-option" >{temp}</div>
        <div className="settings-drop-down-option">{theme}</div>
        <div className="settings-drop-down-option" onClick={() => handleLogout()} >Logout</div>
    </div> : <></>


    return <div onClick={()=>toggleOpen()}>
        <Settings className="settings-icon" />
        {dropDown}
    </div>
}
export default SettingsButton
