import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { selectAllLocations, setUserLocations } from "../Features/locations/locationsSlice";
import { selectCurrentUser } from "../Features/auth/authSlice";
import { useGetLocationsQuery } from "../Features/auth/authApiSlice";

import '../Styles/locations.css';

const Locations = () =>
{
    
    const user = useSelector(selectCurrentUser);
    const {data, error, isLoading} = useGetLocationsQuery({'username': user});


    const locations = isLoading? [] : error ? [] : data.locations; //returns an array named locations from the backend'
    //const dispatch = useDispatch();
    //dispatch(setUserLocations(locations));
    
    const renderedLocations = isLoading ? locations === [] ? <div>Add a location to get started</div> : <div>loading...</div> : locations.map(location =>
        <article className="location" key = {location.id}>
            <h2>{location.latitude},{location.longitude} </h2>
            <h3>{location.city}, {location.province}</h3>
        </article>
    )
    
    return(
        <div className="locationsMainPage">
            {renderedLocations}
            {/* syntax for showing data from backend //data ? data.members : <p>Loading...</p>*/}
        </div>
    )
}

export default Locations;
