import React, { useState } from 'react'
import Select from 'react-select'
import { useGetCountriesQuery, useGetStatesQuery, useGetCitiesQuery } from '../Features/locations/locationApiSlice'

const AddLocation = () => {
  
  const [country, setCountry] = useState();
  const [state, setState] = useState();
  const [city, setCity] = useState();

  const { data, isLoading } = useGetCountriesQuery();
  const countries = isLoading ? [] : data.data; //data is the response from the backend, data.data is the list of countries
  const optionsCountries = [];
  countries.forEach(item => {
    optionsCountries.push({
      value: item.country,
      label: item.country
    });
  });

  const {data: dataStates, isSuccess} = useGetStatesQuery({"country": country});
  const states = isSuccess ? dataStates.data: [];
  const optionsStates = [];
  states.forEach(item => {
    optionsStates.push({
      value: item.state,
      label: item.state
    });
  });

  const {data: dataCities, isSuccess: isSuccessCities} = useGetCitiesQuery({"country": country});
  const cities = isSuccess ? dataCities.data: [];
  const optionsCities = [];
  cities.forEach(item => {
    optionsCities.push({
      value: item.city,
      label: item.city
    });
  });




  const stateSelect = country ? 
    <div>
      <span>State/Province</span>
      <br/>
      <Select options ={optionsStates}/>
    </div> : <></>;
  const citySelect = state ?     
    <div>
      <span>City</span>
      <br/>
      <Select options ={optionsCities}/>
    </div> : <></>;

  return (
    <div>
      <button onClick={()=>console.log(country)}>Check country var</button>
      <button>Add my location</button>
      <br />
      <span>- OR -</span>
      <br />
      <span>Add a location manually</span>
      <br />
      <span> Country </span>
      <Select 
      options={optionsCountries}
      onChange= {((newValue) => {setCountry(newValue.value)})}
      />
      {stateSelect}
      {citySelect}

    </div>
  )
}

export default AddLocation
