import Select from 'react-select'
import { useEffect} from 'react';
import { useGetStatesQuery } from '../../Features/locations/locationApiSlice';

const SelectState = (props) => {

    const { data: stateList, currentData: currentStateList ,isLoading, isSuccess, refetch } = useGetStatesQuery({ "country": props.country });
    const states = isSuccess ? currentStateList ? currentStateList.data : stateList.data : [];
    const optionsStates = [];

    useEffect(() => {
        
    states.forEach(item => {
        optionsStates.push({
            value: item.state,
            label: item.state
        });
    });
    },[currentStateList.data, stateList.data])

    useEffect(() =>{
        refetch();
    },[props.country])

    return (
        <div>
            {console.log(optionsStates)}
            <label htmlFor='state'>State/Province</label>
            <br />
            <Select
                id='state'
                isLoading={isLoading ? true : false}
                options={optionsStates}
                onChange={(newValue) => props.setState(newValue.value)}
            />
        </div>
    )
}

export default SelectState
