import { Settings } from "../../Icons/svgImages/index";
import { logOut,selectCurrentToken } from "../../Features/auth/authSlice";
import { useLogoutMutation } from "../../Features/auth/authApiSlice";

import { useSelector, useDispatch } from "react-redux";

import { useState } from "react";

import '../../Styles/settings.css';
import '../../Styles/searchBar.css'

const SettingsButton = () => {

    const accessToken = useSelector(selectCurrentToken);
    const dispatch = useDispatch();
    const [logout] = useLogoutMutation();

    const [isOpen, setIsOpen] = useState(false);

    const handleLogout = async () => {
        try {
          const message = await logout({ accessToken }).unwrap();
          dispatch(logOut());
          console.log(message);
        }
        catch (err) {
          console.log(err);
          console.log("something went wrong");
        }
      }

    const logoutButton = <span onClick={() => handleLogout()} >Logout</span>
    
    const dropDown = isOpen ? <div className="dropdownBorder">
        <div>temp unit</div>
        <div>theme</div>
        {logoutButton}
    </div> : <></>

    return <div className="settings-icon">
        <Settings className="settings-icon" />
        {dropDown}
    </div>
}
export default SettingsButton
