import { useRef, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import { useDispatch } from 'react-redux';
import { setCredentials } from '../../Features/auth/authSlice';

import { useRegisterMutation, useLazyGetUserLocationsQuery } from '../../Features/auth/authApiSlice';

import '../../Styles/login.css';

import { Link } from 'react-router-dom';

const Register = () => {
    const userRef = useRef();
    const errRef = useRef();
    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [confirmPwd, setConfirmPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const navigate = useNavigate();

    const [register, { isLoading }] = useRegisterMutation();
    const dispatch = useDispatch();

    //useLazyQuery hook allows manual fetching
    const [triggerGetLocations] = useLazyGetUserLocationsQuery();

    useEffect(() => {
        userRef.current.focus();
    }, [])
    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (pwd !== confirmPwd) {
                const newError = "Password is not the same!";
                setErrMsg(newError);
                return;
            }

            const userData = await register({ 'username': user, 'password': pwd }).unwrap();
            const accessToken = userData.access_token;
            dispatch(setCredentials({ user, accessToken }));

            //get user locations from backend
            //and set user locations for application logic
            const payload = triggerGetLocations({ 'username': user }, false).unwrap();
            payload.then(data => {
                const locationsFormattedCorrectly = [];
                data?.locations.forEach(location => locationsFormattedCorrectly.push(formatLocation(location)));
                dispatch(setUserLocations({ "locations": locationsFormattedCorrectly }));
            });


            setUser('');
            setPwd('');
            navigate('/locations')

        }
        catch (err) {
            if (err?.data?.message) {
                setErrMsg(err.data.message);
            } else {
                setErrMsg('No server response.');
            }
            errRef.current.focus();
        }
    }

    const handleUserInput = (e) => setUser(e.target.value);
    const handlePwdInput = (e) => setPwd(e.target.value);
    const handlePwdConfirmInput = (e) => setConfirmPwd(e.target.value);

    const content = isLoading ? <h1 className='login'>loading...</h1> : (
        <section className='login'>
            <p ref={errRef} className={errMsg !== '' ? "errmsg" : "offscreen"}> {errMsg}</p>

            <h1>Register</h1>

            <form className='loginForm' onSubmit={handleSubmit}>
                <label htmlFor='username'>Username:</label>
                <input
                    type="text"
                    id="username"
                    ref={userRef}
                    value={user}
                    onChange={handleUserInput}
                    autoComplete="off"
                    required
                />
                <label htmlFor='password'>Password:</label>
                <input
                    type="password"
                    id="password"
                    onChange={handlePwdInput}
                    value={pwd}
                    required
                />
                <label htmlFor='confirmPassword'>Confirm Password:</label>
                <input
                    type="password"
                    id="confirmPassword"
                    onChange={handlePwdConfirmInput}
                    value={confirmPwd}
                    required
                />
                <button>Register Now</button>
            </form>
            <span>Already have an account? <Link to='/login'>Sign in.</Link></span>

        </section>
    )
    return content;
}

export default Register;
