import Select from 'react-select'
import { useEffect, useState } from 'react';
import { useGetStatesQuery } from '../../Features/locations/locationApiSlice';

const SelectState = (props) => {

    const { data: stateList, currentData: currentStateList, isLoading, isSuccess, isError } = useGetStatesQuery({ "country": props.country }, {refetchOnMountOrArgChange: true});
    const states = isSuccess ? currentStateList ? currentStateList : stateList : [];
    const optionsStates = [];
    states.forEach(item => {
        optionsStates.push({
            value: item.state,
            label: item.state
        });
    });
    const placeholderMessage = 'Select a state or province';
    const loadingMessage = 'Loading States...';

    const handleChange = (newValue) =>{
        props.setState(newValue.value)
    }

    useEffect(()=>{
        if (isError) {
            props.setErrorMessage('No server response');
            refetch();
        }
        else props.setErrorMessage('');
    },[isError])


    return (
        <div>
            <label htmlFor='state'>State/Province</label>
            <br />
            <Select
                id='state'
                placeholder = {placeholderMessage}
                isLoading={isLoading ? true : false}
                loadingMessage={()=>loadingMessage}
                options={optionsStates}
                onChange={newValue => handleChange(newValue)}
                autoFocus={true}
                required={true}
            />
        </div>
    )
}

export default SelectState
